import React from 'react'

export default function MediaAluno(props) {

    const nota1Props = props.nota1
    const nota2Props = props.nota2
    const nota3Props = props.nota3
    let media = 0
    const objOrange = {
        color: 'orange'
    }

    return (
        <div>
            <div>
                <p><b>Aluno</b> : {props.aluno.nome}</p>
                <p><b>RM</b>    : {props.aluno.rm}</p>
                <p><b>Turma</b> : {props.aluno.turma}</p>
                <p><b>NOTA </b> : {media = ((nota1Props + nota2Props + nota3Props) / 3) > 0 && ((nota1Props + nota2Props + nota3Props) / 3) < 6 ? <span style={objOrange}>{media}</span> : <span style={objOrange}>{media}</span>} </p>
            </div>
        </div>
    )
}
